<template lang="">
  <slot> </slot>
</template>

<script>
import { provide, inject, watch, onMounted, onUnmounted, computed } from "vue";

import Transform from "ol-ext/interaction/Transform";

import CopyPaste from "ol-ext/interaction/CopyPaste";

import usePropsAsObjectProperties from "@/composables/usePropsAsObjectProperties";

import { Fill, RegularShape, Stroke, Text, Style } from "ol/style";

export default {
  name: "ol-interaction-transform",
  setup(props) {
    const map = inject("map");
    const vector = inject("vectorLayer");

    // Set cursor style
    Transform.prototype.Cursors["rotate"] =
      "url('data:image/png;base64,') 5 5, auto";
    Transform.prototype.Cursors["rotate0"] =
      "url('data:image/png;base64,') 5 5, auto";

    const { properties } = usePropsAsObjectProperties(props);

    let transform = computed(() => {
      let interaction = new Transform({
        ...properties,
      });

      return interaction;
    });
    console.log("vectorLayer", vector);
    let copypaste = computed(() => {
      let copypaste = new CopyPaste({
        destination: vector.value.getSource(),
        features: transform.value.getFeatures(),
      });
      // Remove selection if cut
      copypaste.on("cut", function() {
        transform.value.select();
      });

      copypaste.on("paste", function(e) {
        transform.value.select();
        e.features.forEach(function(f) {
          transform.value.select(f, true);
        });
      });
      return copypaste;
    });

    // watch(copypaste, (newVal, oldVal) => {
    //   map.removeInteraction(oldVal);
    //   map.addInteraction(newVal);

    //   map.changed();
    // });

    watch(transform, (newVal, oldVal) => {
      map.removeInteraction(oldVal);
      map.addInteraction(newVal);

      map.changed();
    });

    onMounted(() => {
      map.addInteraction(transform.value);
      map.addInteraction(copypaste.value);

      //   // Style the rotate handle
      var circle = new RegularShape({
        fill: new Fill({ color: [255, 255, 255, 0.01] }),
        stroke: new Stroke({ width: 1, color: [0, 0, 0, 0.01] }),
        radius: 8,
        points: 10,
      });

      transform.value.setStyle(
        "rotate",
        new Style({
          text: new Text({
            text: "\u21BA",
            font: "32px Fontawesome",
            textAlign: "left",
            fill: new Fill({ color: "red" }),
          }),
          image: circle,
        })
      );
      // Center of rotation
      transform.value.setStyle(
        "rotate0",
        new Style({
          text: new Text({
            text: "\u21BA",
            font: "20px Fontawesome",
            fill: new Fill({ color: [255, 255, 255, 0.8] }),
            stroke: new Stroke({ width: 2, color: "red" }),
          }),
        })
      );
      // Style the move handle
      transform.value.setStyle(
        "translate",
        new Style({
          text: new Text({
            text: "\u2725",
            font: "20px Fontawesome",
            fill: new Fill({ color: [255, 255, 255, 0.8] }),
            stroke: new Stroke({ width: 2, color: "red" }),
          }),
        })
      );
    });

    onUnmounted(() => {
      map.removeInteraction(transform.value);
      map.removeInteraction(copypaste.value);
    });

    provide("stylable", transform);
    provide("stylable", copypaste);
  },
  props: {
    enableRotatedTransform: {
      type: Boolean,
      default: false,
    },
    condition: {
      type: Function,
    },
    addCondition: {
      type: Function,
    },
    filter: {
      type: Function,
    },
    layers: {
      type: Array,
    },
    hitTolerance: {
      type: Number,
      default: 2,
    },
    translateFeature: {
      type: Boolean,
      default: true,
    },
    scale: {
      type: Boolean,
      default: true,
    },
    rotate: {
      type: Boolean,
      default: true,
    },
    keepAspectRatio: {
      type: Boolean,
      default: false,
    },
    translate: {
      type: Boolean,
      default: true,
    },
    stretch: {
      type: Boolean,
      default: true,
    },
  },
};
</script>

<style lang=""></style>
